import Image from "next/image";

export default function TeamMember({
  avatar,
  name,
  position,
  mail,
  github,
  dribble,
}: {
  avatar: any;
  name: string;
  position: string;
  mail?: string;
  github?: string;
  dribble?: string;
}) {
  return (
    <div className="pl-3 mt-16 flex items-center flex-wrap md:flex-nowrap">
      <div className="flex items-center">
        <Image src={avatar} width={100} height={100} />
        <div className="pl-6 md:w-80 w-auto">
          <p className="font-semibold tracking-wide text-lg">{name}</p>
          <p className="text-sm text-opacity-60 text-black pt-0.5">
            {position}
          </p>
          <div className="flex mt-2">
            {mail && (
              <a
                href={`mailto: ${mail}`}
                className="p-1.5 bg-iconGreen rounded-full bg-opacity-40 mr-7"
              >
                <MailIcon />
              </a>
            )}
            {github && (
              <a
                href={github}
                className="p-1.5 bg-iconPink rounded-full bg-opacity-40 mr-7"
              >
                <GithubIcon />
              </a>
            )}
            {dribble && (
              <a
                href={dribble}
                className="p-1.5 bg-black bg-opacity-60 rounded-full"
              >
                <DribbleIcon />
              </a>
            )}
          </div>
        </div>
      </div>
      <p className="md:w-1/2 mt-6 md:mt-0 w-full pl-0 md:pl-6 font-light md:border-l md:border-opacity-60 md:border-black text-sm">
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Pretium luctus
        netus nulla cursus at ipsum leo proin. Amet at blandit non augue cursus
        aliquam fusce eros mauris.
      </p>
    </div>
  );
}

